<?phprequire_once('auth.php');class ticketAction extends DatabaseAction {	function execute() {		$cart = $this->cart;				if (is_array($cart)) {			if (is_array($cart[$this->item_code])) {				unset($cart[$this->item_code]);			} else {				$cart = array();			}		}		$this->cart = $cart;		if (count($cart)) {			$this->__controller->redirectToAction('cart');						return false;		} else {			$this->__controller->redirectToAction('list');						return false;		}	}}?>