<?phprequire_once('auth.php');class Action extends AuthAction {	function execute() {		$cart = $this->cart;				if (!is_array($cart)) {			$cart = array();		}				if (!is_array($cart[$this->item_code]) && count($cart) === 0) {			$item_code = $this->item_code;			$category_code = $this->category_code;			$quantity = $this->quantity;			$p_date = $this->p_date;			$place_name = $this->place_name;						$cart[$item_code] = array();			$cart[$item_code]['item_code'] = $item_code;			$cart[$item_code]['category_code'] = $category_code;			$cart[$item_code]['quantity'] = $quantity;			$cart[$item_code]['p_date'] = $p_date;			$cart[$item_code]['place_name'] = $place_name;		}		$this->cart = $cart;				$this->__controller->redirectToAction('cart');				return false;	}}?>